const incomingItems = $input.all();


// INTELLIGENT CONTENT SUMMARIZATION (Cost-Optimized)
// Limits to 2000 chars while preserving structure
// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
function intelligentSummarize(sourceContent) {
  if (!sourceContent) return { summary: "No content available", structure: "empty", wordCount: 0,complexity: "unknown" };

  // Extract key structural elements
  const { fullText, sections, extractionStats } = sourceContent;

  let summary = "";
  // Priority 1: Section headings (highest signal-to-noise ratio)
  const sectionTitles = (sections || [])
    .filter(s => s.title && s.title.length > 3) // Skip very short titles
    .map(s => `‚Ä¢ ${s.title}`)
    .slice(0, 12) // Max 12 headings
    .join('\n');

  if (sectionTitles) {
    summary += "**Key Sections:**\n" + sectionTitles + "\n\n";
  }

  // Priority 2: First substantive section content (provides context)
  if (sections && sections.length > 0) {
    // Find first section with actual content (not just heading)
    const contentSection = sections.find(s => 
      s.content && s.content.length > 50
    );

    if (contentSection) {
      summary += "**Core Content Sample:**\n" 
        + contentSection.content.substring(0, 500)
        .replace(/\n+/g, ' ') // Remove line breaks for compactness
        + "...\n";
    }
  }

  // Priority 3: Fallback to fullText if sections are empty
  if (!sections || sections.length === 0) {
    summary = "**Content Preview:**\n" + (fullText || "").substring(0, 1800) + "...";
  }

  // Enforce strict 2000 char limit (API cost control)
  summary = summary.substring(0, 2000);

  return {
    summary,
    structure: extractionStats?.hasToggles ? "hierarchical" : "linear",
    wordCount: extractionStats?.wordCount || 0,
    complexity: extractionStats?.complexity || "unknown",
    sectionCount: sections?.length || 0,
    hasCode: extractionStats?.codeSections > 0,
    hasToggles: extractionStats?.toggleSections > 0
  };
}

// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
// PROCESS AND MERGE
// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
return incomingItems.map(item => {
  const sourceContent = item.json.sourceContent || {};
  const contentSummary = intelligentSummarize(sourceContent);

  return {
    json: {
      // Preserve all original data from previous nodes
      ...item.json,
      // Added personal context
      personalContext,
      // Add optimized content summary for AI prompts
      contentSummary, // This is what AI nodes will use
      // Keep full sourceContent for reference if needed
      _fullSourceContent: sourceContent
    }
  };
});

// INTELLIGENT NOTION CONTENT SUMMARIZATION (Cost-Optimized)
// Limits to 2000 chars while preserving structure
// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
function intelligentSummarize(sourceContent) {
  if (!sourceContent) return { summary: "", structure: "empty" };

  // Extract key structural elements
  const { fullText, sections, extractionStats } = sourceContent;

  // Priority 1: Section headings (most informative)
  let summary = "";
  const sectionTitles = (sections || [])
    .filter(s => s.title && s.title.length > 0)
    .map(s => `‚Ä¢ ${s.title}`)
