// Takes a single Notion item and finds all associated files in Google Drive.

const item = $input.first().json;

// --- 1. Extract Core Information ---
// Property names
const driveFolderUrl = item.property_drive_folder_link;
const sessionId = item.property_session_id;
const twitterDraftUrl = item.property_twitter_draft_url;
const linkedinDraftUrl = item.property_linked_in_draft_url;
const blogDraftUrl = item.property_blog_draft_url;

if (!driveFolderUrl) {
  throw new Error('FATAL: Drive Folder Link is missing from Notion item.');
}

// --- 2. Extract Folder ID from URL ---
const folderIdMatch = driveFolderUrl.match(/folders\/([a-zA-Z0-9_-]+)/);
const folderId = folderIdMatch ? folderIdMatch[1] : null;

if (!folderId) {
  throw new Error(`FATAL: Could not extract Folder ID from URL: ${driveFolderUrl}`);
}

// --- 3. Pass Folder ID to the next node ---
// The next node will list all files in this folder.
return {
  json: {
    notionItem: $input.first().json, // Keep the original Notion data
    sessionId: sessionId,
    folderId: folderId,
     draftUrls: {
      twitter: twitterDraftUrl,
      linkedin: linkedinDraftUrl,
      blog: blogDraftUrl
    }
  }
};

// FINAL, 100% ROBUST ASSET ORGANIZER (Nov 2025 revision)
try {
  // Input from "List Drive Files"
  const files = $input.all();
  // Input from "Extract Folder Details"
  const { notionItem, sessionId } = $('Extract Folder Details').first().json;

  // --- Helpers ---
  // Safely gets a property from either .json or top-level
  function getProp(item, propName) {
    if (item.json && typeof item.json === 'object' && propName in item.json) {
      return item.json[propName];
    }
    if (propName in item) {
      return item[propName];
    }
    return null;
  }
  // Robustly extracts fileId from Google Drive URL
  function extractFileIdFromUrl(url) {
    if (!url) return null;
    let match = url.match(/file\/d\/([a-zA-Z0-9_-]+)/);
    if (match) return match[1];
    match = url.match(/id=([a-zA-Z0-9_-]+)/);
    if (match) return match[1];
    return null;
  }

  // --- Draft Identification (using URLs from Notion) ---
  const drafts = {
    twitter: { fileId: extractFileIdFromUrl(notionItem.property_twitter_draft_url) },
    linkedin: { fileId: extractFileIdFromUrl(notionItem.property_linked_in_draft_url) },
    blog: { fileId: extractFileIdFromUrl(notionItem.property_blog_draft_url) },
    imageTaskList: { fileId: extractFileIdFromUrl(notionItem.property_image_task_list_url) }
  };

  // --- Image Identification (using file list from Drive) ---
  // Acceptable image extensions
  const validExtensions = ['.jpeg', '.jpg', '.png', '.webp'];
  // Sort by asset number in file name (asset-<n>)
  const availableImages = files
    .map(item => {
      const name = getProp(item, 'name');
      const id = getProp(item, 'id');
      // Must have valid name/id and begin with "asset-<n>"
      if (
        !name ||
        !id ||
        !name.match(/^asset-\d+-session_/) || // regex matches asset-number-session
        !validExtensions.some(ext => name.toLowerCase().endsWith(ext))
      ) {
        return null;
      }
      // Extract asset number robustly
      const assetMatch = name.match(/^asset-(\d+)-session_/);
      if (assetMatch) {
        return {
          assetNumber: parseInt(assetMatch[1], 10),
          fileId: id,
          fileName: name
        };
      }
      return null;
    })
    .filter(img => img !== null)
    .sort((a, b) => a.assetNumber - b.assetNumber);

  // --- Debugging: Log edge cases to n8n console (viewable in browser logs)
  console.log('[DEBUG: organize assets] files:', files);
  console.log('[DEBUG: organize assets] availableImages:', availableImages);

  // --- Final Output ---
  return [{
    json: {
      notionItem,
      sessionId,
      assets: {
        availableImages,
        drafts
      }
    }
  }];
} catch (error) {
  return [{
    json: {
      error: true,
      message: "Error in Organize Assets: " + error.message
    }
  }];
}

// This node creates the definitive list of required images for the session.
try {
    const taskListText = $input.first()?.json?.data;
    // If the download failed, the input will be empty. This is expected.
    if (!taskListText) {
        // This is Scenario 1: No images are required for this content.
        return [{ json: { expectedImageNumbers: [] } }];
    }

    const imageNumbers = [];
    // Find all instances of "Asset X" to determine planned images.
    const assetMatches = taskListText.matchAll(/Asset (\d+)/g);
    for (const match of assetMatches) {
        imageNumbers.push(parseInt(match[1]));
    }

    return [{
        json: {
            // Return a unique, sorted list of image numbers.
            expectedImageNumbers: [...new Set(imageNumbers)].sort((a, b) => a - b)
        }
    }];
} catch (error) {
    return [{ json: { error: true, message: "Error parsing image manifest: " + error.message } }];
}

// This node creates a final download queue.
const availableImages = $('Organize Assets').first().json.assets.availableImages || [];

return availableImages.map(img => ({ json: img }));

// FINAL DECISION ENGINE V5.0
// This node analyzes all data and creates a definitive plan for each platform.
try {
  // --- 1. GATHER ALL EVIDENCE ---
  // All data is now guaranteed to be ready from the "Set - All Data Ready" node.
  const { twitterDraft, linkedinDraft, blogDraft, expectedImages } = $input.first().json;

  // --- 2. EXTRACT MARKERS & BUILD GLOBAL CONTEXT ---
  // This helper function finds all image markers (e.g., [1, 2]) in a text.
  const getMarkers = (text) => Array.from(text.matchAll(/<<IMAGE_(\d+)>>/g), m => parseInt(m[1]));

  const twitterMarkers = getMarkers(twitterDraft || '');
  const linkedinMarkers = getMarkers(linkedinDraft || '');
  const blogMarkers = getMarkers(blogDraft || '');

  // This is the "global context"—all markers found across all drafts.
  const allDiscoveredMarkers = [...new Set([...twitterMarkers, ...linkedinMarkers, ...blogMarkers])];
  const manifestHasImages = expectedImages && expectedImages.length > 0;

  // --- 3. THE HIERARCHY OF TRUTH (Per Platform) ---
  // This function decides the image plan for ONE platform.
  const determineImagePlan = (platformMarkers, isSocialPlatform = false) => {

    // SCENARIO 3 (Highest Truth): Markers are present in *this* draft.
    // This is your Rule #3: Obey the markers.
    if (platformMarkers.length > 0) {
      return platformMarkers;
    }

    // SCENARIO 2 (AI Failure Fallback): This draft has no markers, 
    // but we know images *should* exist (either from other drafts or the manifest).
    if (allDiscoveredMarkers.length > 0 || manifestHasImages) {
      if (isSocialPlatform) {
         // For social (Twitter/LinkedIn), just attach the most important image, Asset 1.
        return [1];
      } else {
        // For the blog, attach all expected images.
        return expectedImages || [];
      }
    }

    // SCENARIO 1 (No Images Intended): No markers anywhere, manifest is empty.
    return [];
  };

  // --- 4. CREATE THE FINAL, UNAMBIGUOUS PLAN ---
  const finalPlan = {
    scenario: 'Definitive_Image_Plan_V5.0',
    twitter: { imageNumbers: determineImagePlan(twitterMarkers, true) },
    linkedin: { imageNumbers: determineImagePlan(linkedinMarkers, true) },
    blog: { imageNumbers: determineImagePlan(blogMarkers, false) },
  };

  // --- 5. CREATE THE MASTER DOWNLOAD LIST ---
  // This is a small optimization for your "Prepare Image Downloads" node.
  // We can update that node later to only download images that are *actually* needed.
  finalPlan.allImagesToDownload = [...new Set([
    ...finalPlan.twitter.imageNumbers,
    ...finalPlan.linkedin.imageNumbers,
    ...finalPlan.blog.imageNumbers,
  ])].sort((a, b) => a - b);

  return [ { json: finalPlan } ];

} catch (error) {
  return [{ json: { error: true, message: "Error in Decision Engine: " + error.message, scenario: 'ERROR' } }];
}

// FINAL ROBUST BLOG PARSER & ATTACHER V9.0
try {
  // --- 1. Gather Data ---
  const masterData = $('Set - All Data Ready').first().json;
  const markdownText = masterData.blogDraft;
  if (!markdownText) throw new Error('Blog draft content is empty.');

  const allCachedImages = $('Loop to Download Images').all(); // Ensure this is correct node!
  console.log('Image cache:', allCachedImages.map(img => img.json.fileName));

  // Sanity SEO fields
  const title = masterData.notionItem.property_blog_seo_title || 'Untitled Post';
  const slug = masterData.notionItem.property_blog_slug || '';
  const description = masterData.notionItem.property_blog_seo_description || '';
  const keywords = masterData.notionItem.property_blog_seo_keywords || '';
  if (!slug) throw new Error('BlogSlug property missing.');

  // --- 2. Split Markdown Into Blocks (text + image tags)
  // Regex splits at every <<IMAGE_x>>
  const blockPattern = /<<IMAGE_(\d+)>>/g;
  let lastIdx = 0;
  let match;
  let blocks = [];

  while ((match = blockPattern.exec(markdownText)) !== null) {
    // Text before this image tag
    if (match.index > lastIdx) {
      blocks.push({ type: 'text', content: markdownText.slice(lastIdx, match.index) });
    }
    // Image block for this marker
    blocks.push({ type: 'image', imageNumber: parseInt(match[1]), marker: match[0] });
    lastIdx = blockPattern.lastIndex;
  }
  // Any trailing text after last marker
  if (lastIdx < markdownText.length) {
    blocks.push({ type: 'text', content: markdownText.slice(lastIdx) });
  }

  // --- 3. Process Blocks and Attach Images
  let outputBlocks = [];
  blocks.forEach(block => {
    if (block.type === 'text') {
      // Only push non-empty blocks
      if (block.content && block.content.trim().length > 0) {
        outputBlocks.push({ type: 'text', content: block.content.trim() });
      }
    } else if (block.type === 'image') {
      // Find the correct cached image
      const targetImage = allCachedImages.find(
        img => typeof img.json.fileName === 'string' && img.json.fileName.includes(`asset-${block.imageNumber}`)
      );
      if (!targetImage) {
        throw new Error(`Blog Image asset-${block.imageNumber} missing in cache for marker ${block.marker}`);
      }
      outputBlocks.push({ 
        type: 'image', 
        marker: block.marker, // For debugging
        imageNumber: block.imageNumber,
        binary: targetImage.binary
      });
    }
  });

  // --- 4. Optional: Attach SEO/meta info for Sanity
  return [{
    json: {
      title,
      slug,
      description,
      keywords: keywords.split(',').map(k => k.trim()),
      blocks: outputBlocks // This is the key array for posting!
    }
  }];

} catch (error) {
  return [{ json: { error: true, message: `[Blog Parse]: ${error.message}` } }];
}

try {
  // Get data from the previous node
  const data_node = $('Set - All Data Ready').first().json;
  const markdownText = data_node.twitterDraft;

  // Get the image cache, which is now 100% ready.
  const allCachedImages = $('Loop to Download Images').all();

  // Split by "Tweet X/Y" pattern to separate individual tweets
  // The regex captures "Tweet 1/4", "Tweet 2/4", etc.
  const tweetBlocks = markdownText.match(/\d+\/\d+[\s\S]*?(?=\n\n---\n\nTweet \d+\/\d+|\n\n---\n\n$|$)/g);

  if (!tweetBlocks) throw new Error('No tweet blocks found in Twitter draft.');

  const tweets = tweetBlocks.map((block, index) => {
    // Extract the tweet number from "Tweet X/Y"
    const markerMatch = block.match(/(Tweet \d+\/\d+)/);
    const marker = markerMatch ? markerMatch[1] : null;

    // Find image placeholders like <<IMAGE_1>>
    const imageMatch = block.match(/<<IMAGE_(\d+)>>/);
    let imageBinary = null;

    if (imageMatch) {
      const imageNumber = parseInt(imageMatch[1]);
      const targetImage = allCachedImages.find(img => 
        img.json.fileName.includes(`asset-${imageNumber}-`)
      );

      if (!targetImage) {
        // This will cause the workflow to fail and retry, as you requested.
        throw new Error(`Image asset-${imageNumber} was required but not found in cache.`);
      }

      imageBinary = targetImage.binary.data;
    }

    // Clean the text: remove the marker, image placeholder, and "---" separators
    const cleanText = block
      .replace(/Tweet \d+\/\d+/, '')  // Remove "Tweet X/Y"
      .replace(/<<IMAGE_\d+>>/, '')    // Remove image placeholders
      .replace(/\n\n---\n\n$/, '')    // Remove trailing separator
      .replace(/^\n+/, '')             // Remove leading newlines
      .replace(/\n+$/, '')             // Remove trailing newlines
      .replace(/^(Tweet\s+)?\d+\/\d+\s*\n*/mi, '')  // Remove "Tweet X/Y" or just "X/Y"
      .trim();

    return {
      json: {
        order: index + 1,
        text: cleanText,
        inReplyTo: index > 0,
        imageBinary: imageBinary
      }
    };
  });

  return tweets;
} catch (error) {
  return [{ json: { error: true, message: `[Twitter Parse]: ${error.message}` } }];
}

// PRODUCTION LINKEDIN PARSER & ATTACHER - FIXED v10.0
// Handles "# LinkedIn Draft" header with flexible spacing
try {
  // Get the plan from the decision engine
  const plan = $input.first().json.linkedin;

  // Get the draft text from our master data node
  let fullFileContent = $('Set - All Data Ready').first().json.linkedinDraft;

  if (!fullFileContent) {
    throw new Error('LinkedIn draft content is empty.');
  }

  const allCachedImages = $('Loop to Download Images').all();

  // CRITICAL FIX: Remove "# LinkedIn Draft" header AND the first "---" separator
  // The format is: "# LinkedIn Draft\n \n---\n \n[Content]"
  // We need to handle flexible whitespace around the separator

  // Step 1: Remove the header (case-insensitive, handles extra whitespace)
  fullFileContent = fullFileContent.replace(/^#\s*LinkedIn\s*Draft\s*/i, '').trim();

  // Step 2: Remove the FIRST "---" separator (handles whitespace before/after dashes)
  // This regex matches: optional whitespace, 3+ dashes, optional whitespace
  fullFileContent = fullFileContent.replace(/^\s*-{3,}\s*/, '').trim();

  // Step 3: NOW split by "---" for multiple posts (if strategy requires 2+ posts)
  // Use flexible regex that handles whitespace around separators
  const postBlocks = fullFileContent
    .split(/\s*\n-{3,}\n\s*/)  // Matches: whitespace-newline-dashes-newline-whitespace
    .map(block => block.trim())
    .filter(block => block.length > 20); // Ignore blocks shorter than 20 chars

  if (postBlocks.length === 0) {
    throw new Error('No valid post content found after parsing LinkedIn draft.');
  }

  const postsToExecute = postBlocks.map((block, index) => {
    let imageNumbersToAttach = [];

    // Find all image markers within this specific block
    const markersInThisBlock = Array.from(
      block.matchAll(/<<IMAGE_(\d+)>>/g), 
      m => parseInt(m[1])
    );

    if (markersInThisBlock.length > 0) {
      // Scenario 3: Markers are present in content. Use them.
      imageNumbersToAttach = markersInThisBlock;
    } else if (index === 0 && plan && plan.imageNumbers && plan.imageNumbers.length > 0) {
      // Scenario 2 (Fallback): No markers, but it's the 1st post and plan says attach images
      imageNumbersToAttach = plan.imageNumbers;
    }
    // Scenario 1 (No images) is handled by default (array remains empty)

    // Clean the text: remove image markers
    const cleanText = block.replace(/<<IMAGE_\d+>>/g, '').trim();

    // Validation: Ensure we have meaningful content
    if (cleanText.length < 10) {
      throw new Error(`Post ${index + 1} is too short (${cleanText.length} chars). Possible parsing error.`);
    }

    // Additional validation: Check if post is just the header
    if (cleanText.toLowerCase().includes('linkedin draft') && cleanText.length < 30) {
      throw new Error(`Post ${index + 1} appears to be just the header. Content: "${cleanText}"`);
    }

    // TRUST, BUT VERIFY: For each required image number, find its binary from cache
    const imageBinaries = imageNumbersToAttach.map(num => {
      const targetImage = allCachedImages.find(img => 
        img.json && img.json.fileName && img.json.fileName.includes(`asset-${num}`)
      );
      if (!targetImage) {
        throw new Error(`Image asset-${num} was required but not found in cache.`);
      }
      return targetImage.binary;
    });

    return {
      json: {
        order: index + 1,
        text: cleanText,
        imageCount: imageBinaries.length,
        charCount: cleanText.length,
        imageBinaries: imageBinaries
      }
    };
  });

  // Debug logging
  console.log(`✅ LinkedIn Parser: Generated ${postsToExecute.length} post(s)`);
  postsToExecute.forEach((post, idx) => {
    const preview = post.json.text.substring(0, 50).replace(/\n/g, ' ');
    console.log(`   Post ${idx + 1}: ${post.json.charCount} chars, ${post.json.imageCount} image(s)`);
    console.log(`   Preview: "${preview}..."`);
  });

  return postsToExecute;

} catch (error) {
  console.error('❌ LinkedIn Parse Error:', error);
  return [{ 
    json: { 
      error: true, 
      message: `[LinkedIn Parse]: ${error.message}`,
      stack: error.stack,
      timestamp: new Date().toISOString()
    } 
  }];
}

// FINAL BINARY DATA PREPARATION FOR TWITTER UPLOAD
// This node flattens the nested binary structure for the HTTP Request node.
try {
  const tweetData = $input.first().json;

  // Extract the actual binary data from the nested structure
const actualBinary = tweetData.imageBinary;


  // Return the tweet data with the binary at the correct level
  return [{
    json: {
      text: tweetData.text,
      inReplyTo: tweetData.inReplyTo,
      order: tweetData.order
    },
    binary: {
      // This is the key - we're putting the binary data at the top level
      imageBinary: actualBinary
    }
  }];

} catch (error) {
  return [{
    json: {
      error: true,
      message: `[Prepare Binary for Upload]: ${error.message}`
    }
  }];
}

// UNIFIED TWEET PREPARATION - CORRECTED FOR FLAT DATA STRUCTURE
// Input comes directly from IF node (no Set nodes)
try {
  const input = $input.first().json;

  // Extract text directly - it's already a string from the IF node
  const text = $('SplitInBatches - Loop Tweets').first().json.text|| "";

  // Extract media_id_string if it exists (from image path via Upload Media)
  // If coming from false path (text-only), media_id_string won't exist
  const media_id_string = input.media_id_string || "";

  // Extract inReplyTo flag
  const inReplyTo = $('SplitInBatches - Loop Tweets').first().json.inReplyTo || false;

  // Extract order
  const order = $('SplitInBatches - Loop Tweets').first().json.order || 0;

  // Return the flat, clean structure
  return [{
    json: {
      text: text,
      media_id_string: media_id_string,
      inReplyTo: inReplyTo,
      order: order
    }
  }];

} catch (error) {
  return [{
    json: {
      error: true,
      message: `[Unified Prep]: ${error.message}`,
      text: input?.text || "Error preparing tweet",
      media_id_string: "",
      inReplyTo: false,
      order: 0
    }
  }];
}

// ============================================================
// ADD PARENT TWEET ID - WITH ID CLEANING
// ============================================================

try {
  const tweetData = $input.first()?.json || {};
  const order = tweetData.order || 1;

  console.log(`[Threading] ========================================`);
  console.log(`[Threading] Processing tweet ${order}`);

  let parentTweetId = null;
  let isFirstTweet = false;

  // ============================================================
  // CASE 1: First Tweet (order === 1)
  // ============================================================
  if (order === 1) {
    console.log(`[Threading] ✓ Tweet ${order}: FIRST TWEET`);
    parentTweetId = null;
    isFirstTweet = true;

    return [{
      json: {
        text: tweetData.text,
        media_id_string: tweetData.media_id_string || "",
        inReplyTo: tweetData.inReplyTo || false,
        order: order,
        parentTweetId: parentTweetId,
        isFirstTweet: isFirstTweet
      }
    }];
  }

  // ============================================================
  // CASE 2: Subsequent Tweets (order > 1)
  // ============================================================
  if (order > 1 && tweetData.inReplyTo === true) {
    console.log(`[Threading] → Tweet ${order}: Getting parent tweet ID...`);

    try {
      const prepareNode = $('Prepare for Next Loop').first()?.json;
      console.log(`[Threading] Raw lastTweetId:`, prepareNode?.lastTweetId);
      console.log(`[Threading] Type:`, typeof prepareNode?.lastTweetId);

      if (prepareNode?.lastTweetId) {
        // CRITICAL FIX: Clean the tweet ID thoroughly
        const rawId = prepareNode.lastTweetId;

        // Convert to string and remove all unwanted characters
        parentTweetId = String(rawId)
          .replace(/["\t\n\r\\]/g, '')  // Remove quotes, tabs, newlines, backslashes
          .replace(/\s+/g, '')           // Remove all whitespace
          .trim();                       // Trim edges

        console.log(`[Threading] ✓ Cleaned parent tweet ID: ${parentTweetId}`);
        console.log(`[Threading] ✓ Length: ${parentTweetId.length}`);
        console.log(`[Threading] ✓ Is numeric: ${/^[0-9]+$/.test(parentTweetId)}`);

        // Validate it's a valid tweet ID (numeric, 1-19 digits)
        if (!/^[0-9]{1,19}$/.test(parentTweetId)) {
          console.error(`[Threading] ✗ Invalid tweet ID format after cleaning!`);
          console.error(`[Threading] Cleaned ID: "${parentTweetId}"`);
          console.error(`[Threading] Character codes:`, Array.from(parentTweetId).map(c => c.charCodeAt(0)));
          parentTweetId = null;
        }
      } else {
        console.error(`[Threading] ✗ No lastTweetId found`);
      }
    } catch (error) {
      console.error(`[Threading] ✗ Error:`, error.message);
    }

    isFirstTweet = false;

    if (!parentTweetId) {
      console.error(`[Threading] ✗✗✗ CRITICAL ERROR ✗✗✗`);
      console.error(`[Threading] No valid parent tweet ID for tweet ${order}`);

      return [{
        json: {
          text: tweetData.text,
          media_id_string: tweetData.media_id_string || "",
          inReplyTo: tweetData.inReplyTo,
          order: order,
          parentTweetId: null,
          isFirstTweet: false,
          error: true,
          errorMessage: `Cannot find valid parent tweet ID for tweet ${order}`,
          errorType: 'MISSING_PARENT_ID'
        }
      }];
    }

    console.log(`[Threading] ✓ Tweet ${order} will reply to: ${parentTweetId}`);
  }

  // ============================================================
  // CASE 3: Non-reply tweet
  // ============================================================
  else if (tweetData.inReplyTo === false) {
    console.log(`[Threading] → Tweet ${order}: Standalone tweet`);
    parentTweetId = null;
    isFirstTweet = (order === 1);
  }

  console.log(`[Threading] ✓ Final output - isFirstTweet: ${isFirstTweet}, parentTweetId: ${parentTweetId || 'null'}`);
  console.log(`[Threading] ========================================`);

  return [{
    json: {
      text: tweetData.text,
      media_id_string: tweetData.media_id_string || "",
      inReplyTo: tweetData.inReplyTo,
      order: order,
      parentTweetId: parentTweetId,
      isFirstTweet: isFirstTweet
    }
  }];

} catch (error) {
  console.error(`[Threading] ✗ EXCEPTION:`, error.message);

  const inputData = $input.first()?.json || {};

  return [{
    json: {
      text: inputData.text || "",
      media_id_string: inputData.media_id_string || "",
      inReplyTo: inputData.inReplyTo || false,
      order: inputData.order || 1,
      parentTweetId: null,
      isFirstTweet: (inputData.order === 1),
      error: true,
      errorMessage: `Exception: ${error.message}`,
      errorType: 'EXCEPTION'
    }
  }];
}

// FINAL LINKEDIN DATA PREPARATION V9.1 (with Markdown Cleaning)
// This node flattens the binary array AND cleans the post text.
try {
  const post = $input.first().json;
  let finalBinaryObject = null; // Default to null (text-only post)

  // --- 1. Image Preparation (This part is working perfectly) ---
  if (post.imageBinaries && Array.isArray(post.imageBinaries) && post.imageBinaries.length > 0) {

    // Select only the first image (since the node only supports one)
    const firstImageWrapper = post.imageBinaries[0];
    if (firstImageWrapper && firstImageWrapper.data) {
      finalBinaryObject = firstImageWrapper.data;
    } else if (firstImageWrapper) {
      finalBinaryObject = firstImageWrapper;
    }

    console.log(`[LinkedIn] Image found. Preparing to attach: ${finalBinaryObject?.fileName}`);
  } else {
    console.log('[LinkedIn] No images found for this post. Posting as text-only.');
  }

  // --- 2. Text Cleaning (THE FIX) ---
  let cleanText = post.text;

  // Remove markdown bolding (e.g., **The Problem:** -> The Problem:)
  cleanText = cleanText.replace(/\*\*(.*?)\*\*/g, '$1');

  // Remove markdown italics (e.g., *text* -> text)
  cleanText = cleanText.replace(/\*(.*?)\*/g, '$1');

  // Remove any lingering markdown headers (e.g., ### Title -> Title)
  cleanText = cleanText.replace(/^#+\s+(.*)$/gm, '$1');

  // Replace the arrow emoji with a standard dash or bullet (LinkedIn prefers this)
  cleanText = cleanText.replace(/→/g, '•');

  // Trim any leading/trailing whitespace left over from cleaning
  cleanText = cleanText.trim();

  // --- 3. Final Output Assembly ---
  const outputItem = {
    json: {
      text: cleanText // Pass the CLEANED text
    }
  };

  // If an image was prepared, add it to the 'binary' property
  if (finalBinaryObject) {
    outputItem.binary = {
      "linkedInImage": finalBinaryObject 
    };
  }

  return [outputItem];

} catch (error) {
  return [{
    json: {
      ...$input.first().json, // Pass original data on error
      error: true,
      message: `[Prepare LinkedIn Data]: ${error.message}`
    }
  }];
}

